			if (in_gross=="" || apply_monthly=="" || company=="") {
				alert("Please complete the form.");
				return false;
			}
			$("#step2").css("display","none");
			$("#step3").css("display","block");
			return false;
			});
		$(".next3").click(function(){
			var name=$("#name").val().replace("Name","");
			var phone=$("#phone").val().replace("Phone","");
			var email=$("#email").val().replace("Email","");
			if (name=="" || phone=="" || email=="") {
				alert("Please complete the form.");
				return false;
			}
			$("#step3").css("display","none");
			$("#step4").css("display","block");
			$.post("apply.php", $("#apply_form").serialize(),function(data){document.location.href=data;});
			return false;
			});
		$("input[type=text], textarea").focus(function(){
			clear_text();
			var data=$(this).attr("data");
			if($(this).val().replace(data,"")=='') $(this).val('');
			});
		$("select").focus(function(){
			clear_text();
			});
		
	});
	function clear_text() {
		var the_inputs = $('input[type=text]');
			the_inputs.each(function() {
			    var old_val=$(this).val();
			    if (old_val=="") {
				var new_val=$(this).attr("data");
				$(this).val(new_val);
			    }
			    
			});
	}
</script>
